public class EvenPosition {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] arr = new int[] { 1, 2, 3, 4, 5, 6 };
		
        for (int i = 1; i < arr.length; i = i + 2) {
 
            System.out.println(arr[i]);
			
		}
	}

}